sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},

    whenNeeded: function(date) {
        var whenNeeded = new GlideDateTime();
        var whenNeededCheck = new GlideDateTime(date);


        if (whenNeededCheck.onOrBefore(whenNeeded)) {
            return true;
        } else {
			return false;
		}
    },

    type: 'NeedItUtils'
};
